mapper.update:
    update book
    set author = CASE id
    
      when #{item.id} then #{item.author}
    
    end,
    title =CASE id
    
    when #{item.id} then #{item.title}
    end,
    
    name = CASE id
    
      when #{item.id} then #{item.name}
    
    end

    where id in 
    #{item.id}
